    <style>
    body {
        background-color: white !important;
    }

    .brownBg {
        background-color: #F4EDDC;
    }

    .textColor {
        color: #4C4343;
    }

    .text-block {
        background-color: #E3EBC1;
        border-radius: 50px;
        padding: 12px;
        width: 200px;
        height: 50px;
        margin-bottom: 10px;
    }

    .address {
        display: flex;
        flex-direction: row;
        align-items: center;
        justify-content: space-between;
        flex-wrap: wrap;
        background-color: #F4EDDC;
        padding: 10px;
        border-radius: 10px;
        margin-top: 10px;
        margin-bottom: 10px;
        width: 820px;
        height: auto;
        border-radius: 23px;
    }

    .text-block-II {
        background-color: #E3EBC1;
        border-radius: 50px;
        padding: 12px;
        width: 170px;
        height: 50px;
        margin-top: 20px;
    }

    .cart-container {
        background-color: #FFFFFF;
        padding: 20px;
        border-radius: 10px;
    }

    .cart-item {
        display: flex;
        flex-direction: row;
        align-items: center;
        justify-content: space-between;
        flex-wrap: wrap;
        background-color: #F4EDDC;
        padding: 10px;
        border-radius: 10px;
        margin-bottom: 10px;
        width: 820px;
        height: 100px;
        border-radius: 23px;
    }

    .cart-item img {
        border: 1px solid #4C4343;
        width: 80px;
        height: 80px;
        border-radius: 23px;
        object-fit: cover;
        margin-left: 15px;
    }

    .cart-item-name {
        width: 150px;
        height: auto;
        text-align: center;
        font-size: 1.2em;
        font-weight: bold;
    }

    .cart-item-quantity {
        width: 120px;
        height: 25px;
        text-align: center;
        font-size: 1em;
    }

    .cart-item-price {
        width: 100px;
        height: 25px;
        text-align: center;
        font-size: 1.1em;
        font-weight: bold;
    }

    .cart-total {
        border-radius: 50px;
        color: #FFFFFF;
        margin-left: auto;
        text-align: center;
        font-size: 1.5em;
        font-weight: bold;
        background-color: #C4B590;
        padding: 10px;
        width: 250px;
    }

    .no_address {
        background-color: #b95846;
        border-radius: 50px;
        width: 200px;
        height: 25px;
    }

    .address-select {
        margin-top: 10px;
        background-color: #F4EDDC;
        border-radius: 40px;
        padding: 12px;
        width: 820px;
        height: auto;
        margin-top: 10px;

    }

    .address-select label {
        font-weight: bold;
    }

    .address-select select {
        width: 100%;
        padding: 10px;
        margin-top: 10px;
        border-radius: 5px;
        border: 1px solid #ccc;
        font-size: 1.1em;
    }

    .pay {
        border-radius: 50px;
        color: #4C4343;
        margin-left: auto;
        margin-top: 10px;
        text-align: center;
        font-size: 1.2em;
        font-weight: bold;
        background-color: #ACE094;
        padding: 10px;
        width: 140px;
    }
    </style>




