    <script>
    document.addEventListener('DOMContentLoaded', function() {
        // Function to update address details
        function updateAddressDetails(option) {
            document.getElementById('detail-line1').textContent = option.getAttribute('data-line1');
            document.getElementById('detail-line2').textContent = option.getAttribute('data-line2');
            document.getElementById('detail-city').textContent = option.getAttribute('data-city');
            document.getElementById('detail-district').textContent = option.getAttribute('data-district');
            document.getElementById('detail-sub-district').textContent = option.getAttribute(
                'data-sub-district');
        }

        // Get the initially selected option (first option by default)
        var initialOption = document.querySelector('#address_id option[selected]');
        if (initialOption) {
            updateAddressDetails(initialOption); // Load initial details
        }

        // Update the details when the user selects a different address
        document.getElementById('address_id').addEventListener('change', function() {
            var selectedOption = this.options[this.selectedIndex];
            updateAddressDetails(selectedOption);
        });
    });
    </script>
    <script>
    function updateHiddenAddressId() {
        // Get the select element
        var select = document.getElementById('address_id');
        // Get the value of the selected option
        var selectedAddressId = select.value;

        // Check if a valid address is selected (optional validation)
        if (selectedAddressId) {
            // Update the hidden input value
            document.getElementById('hidden_selected_address').value = selectedAddressId;
        } else {
            alert("Please select a valid address.");
        }
    }
    </script>
